import { useState, useEffect } from "react";
export function useHttp(movieRequest) {
  const [movieData, setMovieData] = useState({});
  const sendRequest = async function () {
    try {
      const request = await fetch(
        `https://api.themoviedb.org/3${movieRequest} `
      );
      setMovieData(await request.json());
    } catch (err) {}
  };
  sendRequest();
  // useEffect(() => {
  //   sendRequest();
  // }, []);
  return {
    movieData,
  };
}
