import React from "react";
import { useState } from "react";
// import { useContext } from "react";
// import ApiContext from "../../store/api-contex";
import { useHttpTrailer } from "../../hooks/useHttpTrailer";

import classes from "./MovieList.module.css";
import Movie from "./Movie";
export default function MovieList({ poster, movData }) {
  const { trailerData, isLoading } = useHttpTrailer(inviMov.id);
  const [pop, setPop] = useState(false);
  const [inviMov, setInviMov] = useState({});
  const infoHandler = (choosenMov) => {
    setInviMov(choosenMov);
    setPop((prevPop) => !prevPop);
  };

  return (
    <div className={classes["mov-list"]}>
      <ul>
        {movData.results.map((data) => (
          <Movie
            infoPop={infoHandler}
            poster={poster}
            key={data.id}
            movData={data}
          ></Movie>
        ))}
      </ul>
      {pop && (
        <div className={classes.info}>
          <section>
            <h3>{inviMov.title}</h3>
            <div>Release Date:{inviMov.release_date}</div>
            <div>Vote : {inviMov.vote_average}</div>
            <div>{inviMov.overview}</div>
          </section>
          <section></section>
        </div>
      )}
    </div>
  );
}
