import { useState, useEffect } from "react";
export function useHttp(movieRequest) {
  const [movieData, setMovieData] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const sendRequest = async function () {
    try {
      const request = await fetch(
        `https://api.themoviedb.org/3${movieRequest} `
      );
      setMovieData(await request.json());

      if (!request.ok) {
        throw new Error("Failed to fetch movie");
      }
    } catch (err) {}
  };
  useEffect(() => {
    console.log("e");
    setIsLoading(true);
    sendRequest();
    setIsLoading(false);
  }, []);
  return {
    movieData,
    isLoading,
  };
}
