import React from "react";

import { useContext } from "react";
import ApiContext from "../../../store/api-contex";
import { useHttp } from "../../../hooks/useHttp";

// import classes from "./TrendingMov.module.css";

export default function TrendingMov() {
  const requests = useContext(ApiContext);
  const { movieData: trendData, isLoading: trendLoad } = useHttp(
    requests.fetchTrending
  );
  if (!trendLoad) {
    return (
      <ul>
        {trendData.results.map((data) => (
          <li>
            <img
              src={`https://image.tmdb.org/t/p/original${data.backdrop_path}`}
              alt=""
            />
          </li>
        ))}
      </ul>
    );
  } else {
    return <div>Loading...</div>;
  }
}
