import React from "react";

import { useContext } from "react";
import ApiContext from "../../store/api-contex";
import { useHttp } from "../../hooks/useHttp";

import classes from "./MovieList.module.css";
import OriginalMov from "./category/OriginalMov";
import TrendingMov from "./category/TredingMov";

export default function MovieList() {
  const requests = useContext(ApiContext);
  const { movieData: ogData, isLoading: ogLoad } = useHttp(
    requests.fetchNetflixOriginals
  );
  const { movieData: trendData, isLoading: trendLoad } = useHttp(
    requests.fetchTrending
  );
  console.log(trendLoad);
  if (!ogLoad) {
    return (
      <div className={classes["mov-list"]}>
        {/* Original Section */}
        <div className={classes.og}>
          {ogLoad || <OriginalMov ogData={ogData}></OriginalMov>}
        </div>
        <div className={classes["the-others"]}>
          <h2>Xu hướng</h2>
          {trendLoad || <TrendingMov trendData={trendData}></TrendingMov>}
        </div>
      </div>
    );
  } else {
    return <div></div>;
  }
}
