import React from "react";
import { useContext } from "react";
import classes from "./Banner.module.css";
import { useHttp } from "../../hooks/useHttp";
import { bannerAccess } from "../../util/bannerAccess.js";
import ApiContext from "../../store/api-contex";

let banner = {};
export default function Banner({ banner }) {
  const requests = useContext(ApiContext);

  // Kiểm tra data đã load xong chưa để lấy banner ra
  const { movieData: ogData, isLoading: ogLoad } = useHttp(
    requests.fetchNetflixOriginals
  );
  console.log(ogData);
  if (!ogLoad) {
    banner = bannerAccess(ogData);
  }
  console.log(banner);
  const imgPath = `https://image.tmdb.org/t/p/original${banner.results.backdrop_path}`;
  return (
    <div className={classes.banner}>
      <img className={classes["banner-img"]} src={imgPath} alt="banner" />
      <div className={classes.overlay}></div>
      <div className={classes.about}>
        <h2>{banner.results.name}</h2>
        <button>Play</button>
        <button>My List</button>
        <p>{banner.results.overview.slice(0, 100)}</p>
      </div>
    </div>
  );
}
