import React from "react";
import classes from "./ResultList.module.css";
import { useHttpTrailer } from "../hooks/useHttpTrailer";
import { trailerExtract } from "../util/trailerAccess.js";

let dataTrailer = {};
export default function ResultList({ searchedData }) {
  const { inviMov, trailerData, isLoading, infoHandler } =
    useHttpTrailer(trailerExtract);
  return (
    <div className={classes["search-container"]}>
      <h2>Search Result</h2>
      <ul className={classes["search-list"]}>
        {searchedData.map((movie) => (
          <li>
            <img
              src={`https://image.tmdb.org/t/p/original${movie.poster_path}`}
              alt=""
              className={classes.poster}
              onClick={() => {
                infoHandler(movie);
              }}
            />
          </li>
        ))}
      </ul>
      {/* Phần hiển thị thông tin khi người dùng click */}
      {inviMov && (
        <div className={classes.info}>
          <section className={classes["trailer-info"]}>
            <h3>{inviMov.title || inviMov.name}</h3>
            <div>Release Date: {inviMov.release_date}</div>
            <div>Vote : {inviMov.vote_average}</div>
            <p>{inviMov.overview}</p>
          </section>
          <section className={classes["trailer-media"]}>
            {/* Nếu có trailer thì hiện trailer, không có sẽ hiện backdrop */}
            {trailerData ? (
              <iframe
                width="100%"
                height="400"
                src={`https://www.youtube.com/embed/${trailerData.key}`}
              ></iframe>
            ) : (
              <div>
                <img
                  src={`https://image.tmdb.org/t/p/original${inviMov.backdrop_path}`}
                  alt=""
                />
              </div>
            )}
          </section>
        </div>
      )}
    </div>
  );
}
