import React from "react";
// import { useState, useEffect } from "react";
import classes from "./Browse.module.css";

import { useContext } from "react";
import ApiContext from "../store/api-contex";
import { useHttp } from "../hooks/useHttp";

import NavBar from "../components/upper/NavBar";
import Banner from "../components/upper/Banner";
import MovieList from "../components/middle/MovieList";

const API_KEY = "292c6915aed6c1ea24bb47d8b5d3ca92";
const requests = {
  fetchTrending: `/trending/all/week?api_key=${API_KEY}&language=en-US`,
  fetchNetflixOriginals: `/discover/tv?api_key=${API_KEY}&with_network=123`,
  fetchTopRated: `/movie/top_rated?api_key=${API_KEY}&language=en-US`,
  fetchActionMovies: `/discover/movie?api_key=${API_KEY}&with_genres=28`,
  fetchComedyMovies: `/discover/movie?api_key=${API_KEY}&with_genres=35`,
  fetchHorrorMovies: `/discover/movie?api_key=${API_KEY}&with_genres=27`,
  fetchRomanceMovies: `/discover/movie?api_key=${API_KEY}&with_genres=10749`,
  fetchDocumentaries: `/discover/movie?api_key=${API_KEY}&with_genres=99`,
  fetchSearch: `/search/movie?api_key=${API_KEY}&language=en-US`,
};

function Browse() {
  const { movieData: ogData, isLoading: ogLoad } = useHttp(
    requests.fetchNetflixOriginals
  );
  const { movieData: trendData, isLoading: trendLoad } = useHttp(
    requests.fetchTrending
  );
  const { movieData: topRatedData, isLoading: topRatedLoad } = useHttp(
    requests.fetchTopRated
  );
  const { movieData: actionData, isLoading: actionLoad } = useHttp(
    requests.fetchActionMovies
  );

  return (
    <ApiContext.Provider value={requests}>
      <div className="app">
        <div className={classes.upper}>
          {" "}
          <NavBar></NavBar>
          {ogLoad || <Banner></Banner>}
        </div>
        <div className={classes.middle}>
          <div className={classes.og}>
            {ogLoad || <MovieList movData={ogData}></MovieList>}
          </div>
          <div className={classes["the-others"]}>
            <h2>Xu hướng</h2>
            {trendLoad || <MovieList movData={trendData}></MovieList>}
          </div>
        </div>
      </div>
    </ApiContext.Provider>
  );
}

export default Browse;
