import React from "react";
import { useState } from "react";
// import { useContext } from "react";
// import ApiContext from "../../store/api-contex";
// import { useHttpTrailer } from "../../hooks/useHttpTrailer";

import classes from "./MovieList.module.css";
import Movie from "./Movie";
let dataTrailer = {};
export default function MovieList({ poster, movData }) {
  const [inviMov, setInviMov] = useState(null);
  const [trailerData, setTrailerData] = useState({});
  async function sendRequest(choosenMov) {
    try {
      // Gửi request lấy trailer
      const request = await fetch(
        `https://api.themoviedb.org/3/movie/${choosenMov.id}/videos?api_key=292c6915aed6c1ea24bb47d8b5d3ca92 `
      );
      dataTrailer = await request.json();
      // Kiểm tra cú click này có trùng movie với lần trước không
      // Nếu trùng thì đóng phần info
      // Nếu không thì hiện info movie khác
      setInviMov((prevMov) => (prevMov === choosenMov ? null : choosenMov));
      // setPop((prevPop) => !prevPop);
      if (!request.ok) {
        throw new Error("Failed to fetch movie");
      }
    } catch (err) {
      console.error(err);
    }
  }

  console.log(dataTrailer);
  return (
    <div className={classes["mov-list"]}>
      <ul>
        {movData.results.map((data) => (
          <Movie
            infoPop={sendRequest}
            poster={poster}
            key={data.id}
            movData={data}
          ></Movie>
        ))}
      </ul>
      {inviMov && (
        <div className={classes.info}>
          <section>
            <h3>{inviMov.title}</h3>
            <div>Release Date:{inviMov.release_date}</div>
            <div>Vote : {inviMov.vote_average}</div>
            <div>{inviMov.overview}</div>
          </section>
          <section></section>
        </div>
      )}
    </div>
  );
}
