import React from "react";
import { useState } from "react";
// import { useContext } from "react";
// import ApiContext from "../../store/api-contex";
// import { useHttpTrailer } from "../../hooks/useHttpTrailer";

import classes from "./MovieList.module.css";
import Movie from "./Movie";
let dataTrailer = {};
export default function MovieList({ poster, movData }) {
  const [inviMov, setInviMov] = useState(null);
  const [trailerData, setTrailerData] = useState({});
  // Hàm tìm trailer
  const trailerExtract = (trailer) => {
    // Tìm Trailer, nếu không có Trailer chuyển sang tìm Teaser
    return (
      trailer.results.find((trail) => trail.type === "Trailer") ||
      trailer.results.find((trail) => trail.type === "Teaser")
    );
  };
  // Hàm xử lý khi ấn vào một bộ phim
  async function sendRequest(choosenMov) {
    try {
      // Gửi request lấy trailer
      const request = await fetch(
        `https://api.themoviedb.org/3/movie/${choosenMov.id}/videos?api_key=292c6915aed6c1ea24bb47d8b5d3ca92 `
      );
      const resData = await request.json();
      // Lấy dữ liệu trailer, nếu không có kết quả nào thoả thì trả về null
      dataTrailer = trailerExtract(resData);

      // Kiểm tra cú click này có trùng movie với lần trước không
      // Nếu trùng thì đóng phần info
      // Nếu không thì hiện info movie khác
      setInviMov((prevMov) => (prevMov === choosenMov ? null : choosenMov));

      if (!request.ok) {
        throw new Error("Failed to fetch movie");
      }
    } catch (err) {
      console.error(err);
      // Nếu không fetch được data trailer thì thực hiện chức năng như bình thường
      // Đồng thời cho dữ liệu trailer là null để hiển thị backdrop thay thế
      dataTrailer = null;
      setInviMov((prevMov) => (prevMov === choosenMov ? null : choosenMov));
    }
  }

  return (
    <div className={classes["mov-list"]}>
      {/* Phần hiển thị list phim */}
      <ul className={classes.movie}>
        {movData.results.map((data) => (
          <Movie
            infoPop={sendRequest}
            poster={poster}
            key={data.id}
            movData={data}
          ></Movie>
        ))}
      </ul>
      {/* Phần hiển thị thông tin khi người dùng click */}
      {inviMov && (
        <div className={classes.info}>
          <section className={classes["trailer-info"]}>
            <h3>{inviMov.title || inviMov.name}</h3>
            <div>Release Date: {inviMov.release_date}</div>
            <div>Vote : {inviMov.vote_average}</div>
            <p>{inviMov.overview}</p>
          </section>
          <section className={classes["trailer-media"]}>
            {/* Nếu có trailer thì hiện trailer, không có sẽ hiện backdrop */}
            {dataTrailer ? (
              <iframe
                width="100%"
                height="400"
                src={`https://www.youtube.com/embed/${dataTrailer.key}`}
              ></iframe>
            ) : (
              <div>
                <img
                  src={`https://image.tmdb.org/t/p/original${inviMov.backdrop_path}`}
                  alt=""
                />
              </div>
            )}
          </section>
        </div>
      )}
    </div>
  );
}
